import React,{ useEffect, useState} from 'react';
import { Box, Button, Stack, TextField, TextFile, Typography} from '@mui/material';
import {  options } from '../utils/fetchData';
import HorizontalScrollbar from './HorizontalScrollbar';
const SearchExercises = () => {
  const [search, setSearch]= useState('');
  const [exercises,setExercises]=useState([])
  const [bodyParts,setBodyParts]=useState([])
  
  try {
     async() => {
      const response = await axios.request(options);
    console.log(response.data);
      
    }
  } catch (error) {
    console.error(error);
  }

  const handleSearch = async ()=>{
      if(search){
        const exercisesData= await fetchData('https://exercisedb.p.rapidapi.com/exercises', exerciseOptions);
        
        const searchedExercises = exercisesData.filter(
          (exercise)=> exercise.name.toLowerCase().includes(search)
          || exercise.target.toLowerCase().includes(search)
          || exercise.equipment.toLowerCase().includes(search)
          || exercise.bodyPart.toLowerCase().includes(search)
          
          )
         setSearch('')
          console.log(searchedExercises,'a')
         setExercises(searchedExercises)
      }
  }
  return (
    
    <Stack alignItems="center" mt="37px"
    justifyContent="center" p="20px">
      <Typography fontWeight={700} sx={{fontSize:{lg:'44px', sx:'30px'}}}
      mb="50px" textAlign="center">
        Awesome Exercises You <br/>
        Should Know
      </Typography>
      <Box position="relative" mb="72px">
        <TextField
        sx={{
          input:{ fontWeight:'700',
          border:'none',borderRadius:
          '4px'},
          width:{lg:'800px', xs:'350px'},
          backgroundColor:"#fff",
          borderRadius:'40px'
        }}
          height="76px"
          value={search}
          onChange={(e)=>setSearch(e.target.value.toLowerCase())}
          placeholder='Search Exercises'
          type="text"
          />
          <Button className="search-btn"
          sx={{
            backgroundColor:"#ff2625",
            color:'#fff',
            textTransform:'none',
            width:{ lg: '175px', xs:'80px'},
            fontSize:{ lg:'20px', xs:'14px'},
            height:'56px',
            position:'absolute',
            right:'0'
                }}
                onClick={handleSearch}>
            search
          </Button>
      </Box>
      <Box sx={{position:'relative',width:'100%',p:'20px'}}>
        <HorizontalScrollbar data={bodyParts}/>
      </Box>
    </Stack>
  )
}

export default SearchExercises;
